import { MaterialCommunityIcons } from '@expo/vector-icons';
export default function App() {
  return (
    <View style={styles.container}>

<View style={styles.topBar}>
  <View style={{flexDirection:"row", alignItems:"center"}}>
    <View style={{flex:1, backgroundColor:"red"}}>

  <Text style={{color:"#fff", fontSize:70, fontWeight:'500'}}>
    Percy Dashboard
  </Text>
    </View>

          <View style={{flex:1,backgroundColor:"blue", alignItems:"flex-end"}}>
            <View style={{flex:1, flexDirection:"row",paddingEnd:40}}> 
            <View style={{alignItems:"center", paddingEnd:20}}>

            <Text style={{color:"#fff", fontSize:30, fontWeight:'400', textAlign:'center'}}>
              Number of {'\n'}
              Crops Targeted:
            </Text>
            <View style={{flexDirection:"row"}}> 
            <MaterialCommunityIcons name="tree" size={50} color="white" style={{alignContent:'flex-end'}}/>

            <Text style={{color:"#fff", fontSize:30,}}>    1</Text>
            </View>
            </View>
            <View style={{alignItems:"center"}}>

            <Text style={{color:"#fff", fontSize:30, fontWeight:'400', textAlign:'center'}}>
              Battery {'\n'}
              100%:
            </Text>
            <View style={{flexDirection:"row", }}> 

            <MaterialCommunityIcons name="battery-70" size={50} color="white" />    
                    </View>
            </View>
            </View>
          </View>
     </View>

  </View>

      <View style={{ flex: 4, backgroundColor: "#fff" }} >


        </View>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {


flex: 1,
flexDirection:'column',
  },
  topBar: {
