import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, SafeAreaView} from 'react-native';
import React, { Component } from 'react';
import Ionicons from '@expo/vector-icons/Ionicons';
import { FontAwesome } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import BasicSpeedometer from 'react-native-speedometer-chart';
import RNSpeedometer from 'react-native-speedometer'

import Speedometer , {
  Background,
  Arc,
  Needle,
  Progress,
  Marks,
  Indicator,
} from 'react-native-cool-speedometer';

export default function App() {
  return (
    <SafeAreaView style={{flex:1}}>
    <View style={styles.container}>

<View style={styles.topBar}>
  <View style={{flexDirection:"row", alignItems:"center"}}>
    <View style={{flex:1, }}>

  <Text style={{color:"#fff", fontSize:70, fontWeight:'500'}}>
    Percy Dashboard
  </Text>
    </View>

          <View style={{flex:1, alignItems:"flex-end"}}>
            <View style={{flex:1, flexDirection:"row",paddingEnd:40}}> 
            <View style={{alignItems:"center", paddingEnd:50}}>

            <Text style={{color:"#fff", fontSize:30, fontWeight:'400', textAlign:'center'}}>
              Number of {'\n'}
              Crops Targeted:
            </Text>
            <View style={{flexDirection:"row"}}> 
            <MaterialCommunityIcons name="tree" size={50} color="white" style={{alignContent:'flex-end'}}/>

            <Text style={{color:"#fff", fontSize:30,}}>    1</Text>
            </View>
            </View>
            <View style={{alignItems:"center"}}>

            <Text style={{color:"#fff", fontSize:30, fontWeight:'400', textAlign:'center'}}>
              Battery {'\n'}
              100%:
            </Text>
            <View style={{flexDirection:"row", }}> 

            <MaterialCommunityIcons name="battery-outline" size={50} color="white" />    
                    </View>
            </View>
            </View>
          </View>
     </View>

  </View>

      <View style={{ flex: 5, backgroundColor: "#fff" }} >
        <View style={{paddingTop:10}}>

      <BasicSpeedometer value={90} totalValue={100} internalColor="#03fc3d" showText text="70" textStyle={{ color: 'black', fontSize:20 }}/>
      <RNSpeedometer value={50} size={100} labels= {[
    {
      name: 'Too Slow',
      labelColor: '#03fc3d',
      activeBarColor: '#03fc3d',
    },
    {
      name: 'Very Slow',
      labelColor: '#ff5400',
      activeBarColor: '#03fc3d  ',
    },
    {
      name: 'Slow',
      labelColor: '#f4ab44',
      activeBarColor: '#f4ab44',
    },
    {
      name: 'n',
      labelColor: '#f2cf1f',
      activeBarColor: '#f2cf1f',
    },
    {
      name: 'Fast',
      labelColor: '#14eb6e',
      activeBarColor: '#14eb6e',
    },
    {
      name: 'Unbelievably Fast',
      labelColor: '#03fc3d',
      activeBarColor: '#00ff6b',
    },
  ]}

  />

       <Speedometer
  value={54}
  max={100}
  angle={160}
  fontFamily='squada-one'
>

  <Background angle={180} />
  <Arc/>

  <Progress/>
  <Marks/>
  <Indicator>
    {(value, textProps) => (
      <Text
        {...textProps}
        fontSize={60}
        fill="#555"
        x={250 / 2}
        y={210}
        textAnchor="middle"
        fontFamily='squada-one'
      >
        {value}k/m
      </Text>
    )}
  </Indicator>
</Speedometer> 
        </View>

        </View>

    </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {


flex: 1,
flexDirection:'column',
  },
  topBar: {
  flex:1,

  backgroundColor: '#03fc3d',
  justifyContent: 'center',

  },
  bottom:{
flex:3,
backgroundColor: '#fff',
alignItems: 'center',
justifyContent: 'center',
  },

});
